import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:google_fonts/google_fonts.dart';class Text_Field1 extends StatelessWidget {  Text_Field1({    Key? key,    required this.MyController,    required this.hintText,    required this.icon,    required this.labelText,    required this.pass,    required this.onChanged,    this.keyboardType, required this.max, required this.counterText,  }) : super(key: key);  final TextEditingController MyController;  final RxString hintText;  final RxString labelText;  final String counterText;  final Widget icon;  final bool pass;  static String text1 = '';  final TextInputType? keyboardType;  final void Function(String)? onChanged;  final int max;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(bottom: 20),      child: Container(        width: 360,        height: 60,        child: TextField(          style: TextStyle(fontSize: 25),            textDirection: TextDirection.rtl,            onChanged: (text) {              onChanged!(text);            },            keyboardType: keyboardType,            obscureText: pass,            controller: MyController,            decoration: InputDecoration(              filled: true,                hintTextDirection: TextDirection.rtl,                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(18),                  borderSide: BorderSide(color: Colors.black, width: 50),                ),                enabledBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(20.0),                  borderSide: BorderSide(                    width: 2,                    color: Colors.black26,                  ),                ),                //errorBorder:InputDecoration.errorText,                prefixIcon: icon,                counterText: counterText,                counterStyle:GoogleFonts.tajawal(fontSize: 15,fontWeight: FontWeight.w500),                hintText: " $hintText    ",                hintStyle:GoogleFonts.tajawal(fontSize: 20,foreground: Paint()),                contentPadding: const EdgeInsets.only(left: 5,right: 5,top: 20,bottom: 0)),            maxLines: max,            onTap: () {}        ),      ),    );  }}